import React, { useState } from 'react';
import axios from 'axios';

function IntakeForm({ setResult, setMemory }) {
  const [file, setFile] = useState(null);
  const [text, setText] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const formData = new FormData();
    if (file) formData.append('file', file);
    if (text) formData.append('text', text);
    try {
      const res = await axios.post('http://localhost:8000/intake', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setResult(res.data);
      // Fetch memory log after submission
      const memRes = await axios.get('http://localhost:8000/memory');
      setMemory(memRes.data);
    } catch (err) {
      setResult({ error: 'Submission failed' });
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 24 }}>
      <div style={{ marginBottom: 18 }}>
        <label style={{ fontWeight: 500, color: '#1976d2', marginBottom: 6, display: 'block' }}>Upload PDF or JSON</label>
        <input type="file" accept=".pdf,.json,application/pdf,application/json" onChange={e => setFile(e.target.files[0])} style={{ border: 'none', background: '#fff', fontSize: 16 }} />
      </div>
      <div style={{ marginBottom: 18 }}>
        <label style={{ fontWeight: 500, color: '#1976d2', marginBottom: 6, display: 'block' }}>Or paste email/text</label>
        <textarea value={text} onChange={e => setText(e.target.value)} rows={5} style={{ width: '100%', fontSize: 16, borderRadius: 8, border: '1px solid #ddd', padding: 10, outline: 'none', boxShadow: '0 1px 2px rgba(0,0,0,0.03)' }} />
      </div>
      <button type="submit" disabled={loading} style={{
        background: loading ? '#90caf9' : '#1976d2',
        color: '#fff',
        border: 'none',
        borderRadius: 8,
        padding: '12px 32px',
        fontSize: 18,
        fontWeight: 600,
        cursor: loading ? 'not-allowed' : 'pointer',
        boxShadow: '0 2px 8px rgba(25, 118, 210, 0.08)',
        transition: 'background 0.2s',
      }}>
        {loading ? 'Processing...' : 'Submit'}
      </button>
    </form>
  );
}

export default IntakeForm;
