import React from 'react';

function ResultDisplay({ result }) {
  if (!result) return (
    <div style={{ textAlign: 'center', color: '#aaa', margin: '32px 0' }}>
      <span role="img" aria-label="waiting" style={{ fontSize: 32 }}>üìù</span>
      <div style={{ marginTop: 8 }}>Submit a file or text to see results here.</div>
    </div>
  );
  return (
    <div style={{ background: '#fff', padding: 20, borderRadius: 12, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', marginBottom: 24 }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: 8 }}>
        <span role="img" aria-label="result" style={{ fontSize: 24, marginRight: 8 }}>‚úÖ</span>
        <h3 style={{ margin: 0, color: '#1976d2', fontWeight: 600 }}>Extraction Result</h3>
      </div>
      <pre style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-all', background: '#f6f8fa', borderRadius: 8, padding: 12, fontSize: 15, margin: 0 }}>{JSON.stringify(result, null, 2)}</pre>
    </div>
  );
}

export default ResultDisplay;
