import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Table, TableBody, TableRow, TableCell } from '@mui/material';

function ErpDetailView({ doc, onClose, onAction }) {
  let fields = {};
  try {
    fields = JSON.parse(doc.extracted_fields.replace(/'/g, '"'));
  } catch {
    fields = { raw: doc.extracted_fields };
  }

  return (
    <Dialog open onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>Document Details (ID: {doc.id})</DialogTitle>
      <DialogContent>
        <Table>
          <TableBody>
            {Object.entries(fields).map(([k, v]) => (
              <TableRow key={k}>
                <TableCell>{k}</TableCell>
                <TableCell>{typeof v === 'object' ? JSON.stringify(v) : v}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => onAction(doc.id, 'Approved')}>Approve</Button>
        <Button onClick={() => onAction(doc.id, 'Sent to Accounting')}>Send to Accounting</Button>
        <Button onClick={() => onAction(doc.id, 'Flagged')}>Flag for Review</Button>
        <Button onClick={onClose}>Close</Button>
      </DialogActions>
    </Dialog>
  );
}

export default ErpDetailView;
